document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("donation-form"),t=document.getElementById("progress-fill"),d=document.getElementById("progress-meta"),n=0;e.addEventListener("submit",a=>{a.preventDefault();let o=Number(e.amount_custom.value||(e.amount.value?e.amount.value:0));n+=o;let l=Math.min(n/1e3*100,100);t.style.width=`${l}%`,d.textContent=`R$ ${n} arrecadados de R$ 1000 (${Math.round(l)}%)`,alert(`Obrigado pela doa\xe7\xe3o de R$${o.toFixed(2)}!`)})});